INSERT INTO `wp_comments` (`comment_ID`, `comment_post_ID`, `comment_author`, `comment_author_email`, `comment_author_url`, `comment_author_IP`, `comment_date`, `comment_date_gmt`, `comment_content`, `comment_karma`, `comment_approved`, `comment_agent`, `comment_type`, `comment_parent`, `user_id`) VALUES
(2, 1, 'Atropos', 'atropos@tamrielfoundry.com', '', '::1', '2013-08-03 18:26:26', '2013-08-04 01:26:26', 'This is a sample comment on the post, "Welcome to Tamriel Foundry".', 0, '1', 'Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/28.0.1500.95 Safari/537.36', '', 0, 1),
(3, 42, 'Atropos', 'atropos@tamrielfoundry.com', '', '::1', '2013-08-04 00:24:25', '2013-08-04 07:24:25', 'Here is another seed comment, this time on the new post. This comment features a number of formatting options including <strong>bold text</strong>, <em>italicized words</em>, a <a href="http://tamrielfoundry.com" title="test" target="_blank" rel="nofollow">hyperlink</a> back to the website, and an ordered list:\n\n<ol>\n	<li>one</li>\n	<li>two</li>\n	<li>three</li>\n	<li>four</li>\n	<li>five</li>\n</ol>', 0, '1', 'Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/28.0.1500.95 Safari/537.36', '', 0, 1),
(4, 40, 'Atropos', 'atropos@tamrielfoundry.com', '', '::1', '2013-08-04 00:26:25', '2013-08-04 07:26:25', 'This comment uses some more unusual elements.\n\n<blockquote>This passage of text is a block quote.</blockquote>\n\nThe following is an unordered list:\n\n<ul>\n	<li>one</li>\n	<li>two</li>\n	<li>three</li>\n	<li>four</li>\n	<li>five</li>\n</ul>', 0, '1', 'Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/28.0.1500.95 Safari/537.36', '', 0, 1),
(5, 1, 'TestUser', 'aaa@bbb.ccc', '', '::1', '2013-08-05 22:18:09', '2013-08-06 05:18:09', 'Quoted for truth:\r\n\r\n[quote author="Atropos|comment-2|August 3, 2013"]\r\nThis is a sample comment on the post, “Welcome to Tamriel Foundry”.\r\n[/quote]\r\n\r\nThanks Atropos, happy to be here!', 0, '1', 'Mozilla/5.0 (Windows NT 6.1; WOW64; rv:22.0) Gecko/20100101 Firefox/22.0', '', 0, 2),
(6, 1, 'Atropos', 'atropos@tamrielfoundry.com', '', '::1', '2013-08-05 22:30:07', '2013-08-06 05:30:07', 'I hope your stay is long and pleasant!', 0, '1', 'Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/28.0.1500.95 Safari/537.36', '', 0, 1),
(7, 40, 'Atropos', 'atropos@tamrielfoundry.com', '', '::1', '2013-08-05 22:32:13', '2013-08-06 05:32:13', 'Here are some other useful elements to test in comments:\r\n<ol>\r\n	<li><span style="line-height: 16px;">ordered</span></li>\r\n	<li>lists</li>\r\n</ol>\r\nText\r\n<p style="text-align: center;">with</p>\r\n<p style="text-align: right;">different</p>\r\n<p style="text-align: center;">alignments</p>\r\n\r\n<pre>preformatted code</pre>\r\n<span style="color: #333399;">Colored text</span>\r\n\r\n<del>strikethrough</del>', 0, '1', 'Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/28.0.1500.95 Safari/537.36', '', 0, 1),
(8, 40, 'Atropos', 'atropos@tamrielfoundry.com', '', '::1', '2013-08-05 22:33:30', '2013-08-06 05:33:30', 'Also, sometimes we have to worry about lists within lists:\r\n<ul>\r\n	<li><span style="line-height: 16px;">Nested ordered lists</span>\r\n<ul>\r\n	<li>inner 1</li>\r\n	<li>inner 2</li>\r\n</ul>\r\n</li>\r\n	<li>outer 2</li>\r\n	<li>outer 3</li>\r\n</ul>\r\n&nbsp;\r\n<ol>\r\n	<li><span style="line-height: 16px;">Nested ordered list</span>\r\n<ul>\r\n	<li>inner 1</li>\r\n	<li>inner 2</li>\r\n</ul>\r\n</li>\r\n	<li>outer 2</li>\r\n	<li>outer 3</li>\r\n</ol>', 0, '1', 'Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/28.0.1500.95 Safari/537.36', '', 0, 1);
